mapper.select:
SELECT * FROM question_categories 
WHERE parent_id = #{parentId}
ORDER BY sort_order, id

mapper.insert:
INSERT INTO question_categories (
    name, parent_id, level, sort_order, description, status
) VALUES (
    #{name}, #{parentId}, #{level}, #{sortOrder}, #{description}, #{status}
)

mapper.select:
SELECT COUNT(*) FROM question_categories 
WHERE name = #{name} AND parent_id = #{parentId}

    AND id != #{excludeId}

mapper.update:
UPDATE question_categories 
SET sort_order = #{sortOrder}
WHERE id = #{id}
